mapper.select:
SELECT 

FROM gen_table a

WHERE a.id = #{id}

SELECT
FROM gen_table a
WHERE a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name = #{name}
AND a.name LIKE '%'||#{nameLike}||'%' '%'+#{nameLike}+'%' concat('%',#{nameLike},'%')
AND a.comments LIKE '%'||#{comments}||'%' '%'+#{comments}+'%' concat('%',#{comments},'%')
AND a.parent_table = #{parentTable}
ORDER BY a.name ASC

mapper.update:
UPDATE gen_table SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
